(function(){tinymce.PluginManager.requireLangPack('diyTable');tinymce.create('tinymce.plugins.diyTablePlugin',{init:function(ed,url){ed.addCommand('mceInsert_diyTable',function(){ed.windowManager.open({file:url+'/table.htm',width:400+parseInt(ed.getLang('diyTable.delta_width',0)),height:320+parseInt(ed.getLang('diyTable.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('diyTable',{title:'diyTable.desc',cmd:'mceInsert_diyTable',image:url+'/img/example.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('example',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'sohu diyTable plugin',author:'Levin',authorurl:'http://vivasky.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"};}});tinymce.PluginManager.add('diyTable',tinymce.plugins.diyTablePlugin);})();